mapper.insert:
INSERT INTO NOTICE(
    TITLE,
    CONTENT,
    WRITER_ID
) VALUES (
    #{title},
    #{content},
    (
        SELECT M.ID
        FROM MEMBER M
        WHERE M.NICKNAME = #{nickname}
    )
)

mapper.update:
UPDATE NOTICE
    SET
        TITLE = #{title},
        CONTENT = #{content},
        MODDATE = #{moddate}
    WHERE ID = #{id}

mapper.select:
SELECT N.ID , N.TITLE , N.CONTENT , N.WRITER_ID , M.NICKNAME , N.REGDATE , N.MODDATE , N.CNT FROM NOTICE N JOIN MEMBER M ON N.WRITER_ID = M.ID -- mybatis 동적쿼리 사용하기 WHERE 1=1
AND ( N.TITLE LIKE CONCAT('%', ${search.searchKeyword}, '%') OR N.CONTENT LIKE CONCAT('%', ${search.searchKeyword}, '%') OR M.NICKNAME LIKE CONCAT('%', ${search.searchKeyword}, '%') )
LIMIT #{cri.amount}
